const getPostByTemplate=(id,title,description,url)=>`<div class="post" --data-index="${id}">\n            <h1>${title}</h1>\n            <span class="description">\n                ${description}\n            </span>\n            <br><button class="btn-custom post-read" --data-url="${url}">\n                Read post\n            </button>\n        </div>`,posts=[{id:0,title:"Welcome to my site!",description:"This post doesn't actually exist<br/>it will be made later.",url:"https://km127pl.us",visible:!0}];document.addEventListener("DOMContentLoaded",()=>{loadThemeFromPreferences();const buttons=document.getElementsByClassName("btn-custom");for(const index in Object.keys(buttons)){const button=buttons[index];button.addEventListener("click",event=>{location.href=event.target.getAttribute("--data-url")})}const _templates=posts.map(post=>post.visible?getPostByTemplate(post.id,post.title,post.description,post.url):null),blogSection=document.getElementById("blog");blogSection.innerHTML=`<h1>Latest blog posts</h1>${_templates.join("")}`;const _posts=document.getElementsByClassName("post");for(const index in Object.keys(_posts)){const post=_posts[index];post.style.left=`${25*index}%`}parseCustomButtons()});